const output = $input.first().json.output;

// Split into lines
const lines = output.split("\n");

// Extract main fields
let userName = null;
let mailId = null;
let password = null;
let deposit = null;
let balance = null;

// Collect expense data lines
let expenseData = [];
let expenseSection = false;

for (let line of lines) {
  const lower = line.toLowerCase().trim();

  if (lower.startsWith("user name")) {
    userName = line.split(":")[1].trim();
  } else if (lower.startsWith("mail id")) {
    mailId = line.split(":")[1].trim();
  } else if (lower.startsWith("password")) {
    password = line.split(":")[1].trim();
  } else if (lower.startsWith("deposit amount")) {
    deposit = Number(line.split(":")[1].trim());
  } else if (lower.startsWith("expence data")) {
    expenseSection = true;
  } else if (lower.startsWith("balence") || lower.startsWith("balance")) {
    balance = Number(line.split(":")[1].trim());
    expenseSection = false; // stop collecting expense data
  } else if (expenseSection) {
    expenseData.push(line);
  }
}

// Final structured result
const result = {
  userName,
  mailId,
  password,
  deposit,
  expenseData: expenseData.join("\n").trim(),
  balance
};

return result;

